import React, { useState } from "react";
import axios from "axios";

const CryptoStats = () => {
  const [coin, setCoin] = useState("bitcoin");
  const [stats, setStats] = useState(null);
  const [error, setError] = useState("");

  const fetchStats = async () => {
    setError("");
    try {
      const response = await axios.get(
        `${process.env.REACT_APP_BACKEND_URL}/stats`,
        { params: { coin } }
      );
      setStats(response.data);
    } catch (err) {
      setError("Failed to fetch stats. Please try again later.");
    }
  };

  return (
    <div className="container">
      <h2>Cryptocurrency Stats</h2>
      <select value={coin} onChange={(e) => setCoin(e.target.value)}>
        <option value="bitcoin">Bitcoin</option>
        <option value="matic-network">Matic</option>
        <option value="ethereum">Ethereum</option>
      </select>
      <button onClick={fetchStats}>Fetch Stats</button>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {stats && (
        <div className="result">
          <p>Price: ${stats.price}</p>
          <p>Market Cap: ${stats.marketCap}</p>
          <p>24h Change: {stats["24hChange"]}%</p>
        </div>
      )}
    </div>
  );
};

export default CryptoStats;
