import React, { useState } from "react";
import axios from "axios";

const CryptoDeviation = () => {
  const [coin, setCoin] = useState("bitcoin");
  const [deviation, setDeviation] = useState(null);
  const [error, setError] = useState("");

  const fetchDeviation = async () => {
    setError("");
    try {
      const response = await axios.get(
        `${process.env.REACT_APP_BACKEND_URL}/deviation`,
        { params: { coin } }
      );
      setDeviation(response.data.deviation);
    } catch (err) {
      setError("Failed to fetch deviation. Please try again later.");
    }
  };

  return (
    <div className="container">
      <h2>Cryptocurrency Price Deviation</h2>
      <select value={coin} onChange={(e) => setCoin(e.target.value)}>
        <option value="bitcoin">Bitcoin</option>
        <option value="matic-network">Matic</option>
        <option value="ethereum">Ethereum</option>
      </select>
      <button onClick={fetchDeviation}>Fetch Deviation</button>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {deviation && (
        <div className="result">
          <p>Standard Deviation: ${deviation}</p>
        </div>
      )}
    </div>
  );
};

export default CryptoDeviation;
